uint getScreenHeight() {
	return LCD_H;
}

uint getScreenWidth() {
	return LCD_W;
}

void drawRectangle(uint x, uint y, uint height, uint width, u16 color) {
	LCDFill(x, y, x + height - 1, y + width - 1, color);
}

void drawBitmap(uint x, uint y, uint height, uint width, u16 *bitmap) {
	LCDBitmap(x, y, x + width - 1, y + height - 1, bitmap);
}

u16* getNumberBitmap(uint number, u16 backgroundColor, u16 numberColor) {
	if (number > 9) {
		logError("Number should be less than 9 to obtain valid bitmap: %u",
				number);
		return NULL;
	}

	uint tilemapInitialPos = number * NUMBER_TILEMAP_TILE_WIDTH;

	for (uint y = 0; y < NUMBER_TILEMAP_HEIGHT; y++)
		for (uint x = 0, i = tilemapInitialPos; x < NUMBER_TILEMAP_TILE_WIDTH;
				x++, i++) {
			if (NumberTilemap[(y * NUMBER_TILEMAP_WIDTH) + i]
					== NUMBER_TILEMAP_NUMBER_COLOR)
				numberBitmap[(y * NUMBER_TILEMAP_TILE_WIDTH) + x] = numberColor;
			else
				numberBitmap[(y * NUMBER_TILEMAP_TILE_WIDTH) + x] =
						backgroundColor;

		}

	return &numberBitmap[0];
}

void drawNumber(uint x, uint y, uint number, u16 backgroundColor,
		u16 numberColor) {
	uint n = 0;
	uint coeff = 0;
	while (number > 9) {
		coeff = 1;
		n = number;
		number = 0;
		// get the left most digit while keeping the right part in number
		while (n > 9) {
			number = number + ((n % 10) * coeff);
			n = n / 10;
			coeff = coeff * 10;
		}
		// draw the digit
		drawBitmap(x, y, NUMBER_TILEMAP_HEIGHT, NUMBER_TILEMAP_TILE_WIDTH,
				getNumberBitmap(n, backgroundColor, numberColor));

		x += NUMBER_TILEMAP_TILE_WIDTH;
	}

	drawBitmap(x, y, NUMBER_TILEMAP_HEIGHT, NUMBER_TILEMAP_TILE_WIDTH,
			getNumberBitmap(number, backgroundColor, numberColor));
}
